mapper.update:
UPDATE t_article_label
SET is_deleted = 0
WHERE article_id = #{articleId} AND label_id IN

    #{labelId}

AND is_deleted = 1;

mapper.insert:
INSERT INTO t_article_label (article_id, label_id, is_deleted) VALUES
(#{label.articleId}, #{label.labelId}, 0)
ON DUPLICATE KEY UPDATE is_deleted = IF(is_deleted = 1, 0, is_deleted);

mapper.select:
SELECT * FROM t_article_label WHERE article_id IN
#{articleId}
AND is_deleted = 0
