mapper.select:
SELECT a.* FROM t_article a LEFT JOIN t_article_label al ON a.article_id = al.article_id LEFT JOIN t_label l ON al.label_id = l.label_id WHERE a.is_deleted = 0 AND al.is_deleted = 0 AND l.is_deleted = 0
AND a.article_title LIKE CONCAT('%', #{query.articleTitle}, '%')
AND a.article_content LIKE CONCAT('%', #{query.articleContent}, '%')
AND (
l.label_name = #{labelName}
)
ORDER BY a.update_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(DISTINCT a.article_id) FROM t_article a LEFT JOIN t_article_label al ON a.article_id = al.article_id LEFT JOIN t_label l ON al.label_id = l.label_id WHERE a.is_deleted = 0 AND al.is_deleted = 0 AND l.is_deleted = 0
AND a.article_title LIKE CONCAT('%', #{query.articleTitle}, '%')
AND a.article_content LIKE CONCAT('%', #{query.articleContent}, '%')
AND (
l.label_name = #{labelName}
)
